import React, { Component } from 'react'
import { NavComponents } from './NavComponents'

export default class Sidenav extends Component {

    addToolBar = (index, title) => {
        const icon = document.getElementsByClassName('icons')[index]
        const div = document.createElement('div')
        div.id = index
        div.innerText = title
        icon.appendChild(div)
    }

    removeToolBar=(index)=>{
        const icon = document.getElementsByClassName('icons')[index]
        icon.removeChild(index)
    }

    render() {
        return (
            <div className="side-nav">
                {NavComponents.map((x, i) => <div
                    className="icons"
                    key={x.id}
                    onMouseEnter={() => this.addToolBar(i, x.title)}
                    onMouseLeave={()=>this.removeToolBar(i)}
                >
                    {x.icon}
                </div>)}
            </div>
        )
    }
}
